import React from "react";
import { useContext } from "react";
import Book from "./Book";
import Shelf from "./Shelf";
import Books from "./withFetchooks";
function CurrentReading() {
  const [books] = useContext(Books);
  console.log(books.books.filter((x) => x.shelf === "currentlyReading"));
  return (
    <React.Fragment>
      <Shelf section={"Currently Reading"}>
        {books.books.filter((x) => x.shelf === "currentlyReading").map(b => {
          return (
            <li>
              <Book
                title={b.title}
                authors={b.authors[0]}
                image={
                  b.imageLinks
                }
              />
            </li>
          );
        })}
        <ol className="books-grid">
          <li>
            <Book
              title={"To Kill a Mockingbird"}
              authors={"Harper Lee"}
              image={
                "http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api"
              }
            />
          </li>
          <li>
            <Book
              title={"Ender's Game"}
              authors={"Orson Scott Card"}
              image={
                "http://books.google.com/books/content?id=yDtCuFHXbAYC&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE72RRiTR6U5OUg3IY_LpHTL2NztVWAuZYNFE8dUuC0VlYabeyegLzpAnDPeWxE6RHi0C2ehrR9Gv20LH2dtjpbcUcs8YnH5VCCAH0Y2ICaKOTvrZTCObQbsfp4UbDqQyGISCZfGN&source=gbs_api"
              }
            />
          </li>
        </ol>
      </Shelf>
    </React.Fragment>
  );
}

export default CurrentReading;
