import { useEffect, useState } from "react";
import axios from "axios";

function AddBooks() {
  const [booksAdded, setBooksAdded] = useState({
    title: "",
    authors: "",
    image: "",
    shelf: "",
  });
  useEffect(() => {
    const bookData = {
      title: "Ender's Game",
      authors: "Orson Scott Card",
      image:
        "http://books.google.com/books/content?id=yDtCuFHXbAYC&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE72RRiTR6U5OUg3IY_LpHTL2NztVWAuZYNFE8dUuC0VlYabeyegLzpAnDPeWxE6RHi0C2ehrR9Gv20LH2dtjpbcUcs8YnH5VCCAH0Y2ICaKOTvrZTCObQbsfp4UbDqQyGISCZfGN&source=gbs_api",
      shelf: "none",
    };
    const dataToBeSent = `${bookData}`;
    let token = localStorage.token;

    if (!token)
      token = localStorage.token = Math.random().toString(36).substr(-8);

    let headers = {
      Accept: "application/json",
      ContentType: "application/json",
      Authorization: token,
    };
    fetch(`https://reactnd-books-api.udacity.com/search`, {
      method: "POST",
      headers,
      body: JSON.stringify({ dataToBeSent }),
    })
      // .then((res) => res.json())
      .then((res) => {
        setBooksAdded(res);
        console.log(res);
      });
  }, []);
  return [booksAdded, setBooksAdded];
}

export default AddBooks;
