    title: "",
    authors: "",
    image: "",
    shelf: "",
  });
  useEffect(() => {
    const bookData = {
      allowAnonLogging: true,
      authors: ["Robert Galbraith"],
      averageRating: 3.5,
      canonicalVolumeLink:
        "https://market.android.com/details?id=book-evuwdDLfAyYC",
