      id: "evuwdDLfAyYC",
      imageLinks: {
        smallThumbnail:
          "http://books.google.com/books/content?id=evuwdDLfA…=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",
        thumbnail:
          "http://books.google.com/books/content?id=evuwdDLfA…=frontcover&img=1&zoom=1&edge=curl&source=gbs_api",
      },
      industryIdentifiers: (2)["authoerrrrrrrr"],
      infoLink:
        "https://play.google.com/store/books/details?id=evuwdDLfAyYC&source=gbs_api",
      language: "en",
      maturityRating: "NOT_MATURE",
      pageCount: 464,
      panelizationSummary: {
        containsEpubBubbles: false,
        containsImageBubbles: false,
      },
      previewLink:
        "http://books.google.com/books?id=evuwdDLfAyYC&printsec=frontcover&dq=rowling&hl=&cd=4&source=gbs_api",
      printType: "BOOK",
      publishedDate: "2013-04-30",
      publisher: "Mulholland Books",
      ratingsCount: 3037,
      readingModes: { text: true, image: false },
      shelf: "none",
      title: "The Cuckoo's Calling",
    };
    // const dataToBeSent = `${bookData}`;
    search(`${bookData}`).then((res) => {
      setBooksAdded(res);
    });
  }, []);
  return [booksAdded, setBooksAdded];
}

export default AddBooks;
