import { useEffect, useState } from "react";
import { getAll } from "../BooksAPI";

function GetAllBooks() {
  const [books, setBooks] = useState({
    loading: true,
    books: [],
    error: "",
  });
  useEffect(() => {
    getAll().then((res) => {
      setBooks({
        loading: false,
        books: res,
        error: "",
        shelf: res.map((b) => b.shelf),
      });
    });
  }, []);
  return [books, setBooks];
}

export default GetAllBooks;
