import { useState, useContext,use } from "react";
import { update } from "../BooksAPI";
import { useNavigate } from "react-router-dom";
import Books from "../context/withFetcbooks";

function Book({ book, title, authors, image, value = "none" }) {
  const [books] = useContext(Books);

  // console.log(books);

  const [shelf, setShelf] = useState(value);
  const navigate = useNavigate();

  const changeShelf = (updatedShelf) => {
    setShelf(updatedShelf);
  };
  useEffect(() => {
    update(book, shelf).then((res) => {
      console.log(shelf);
    });
  }, [shelf]);
  return (
    <div className="book">
      <div className="book-top">
        <div
          className="book-cover"
          style={{
            width: 128,
            height: 193,
            backgroundImage: `url(${image})`,
          }}
        ></div>
        <div className="book-shelf-changer">
          <select value={shelf} onChange={(e) => changeShelf(e.target.value)}>
            <option value="null" disabled>
              Move to...
            </option>
            <option value="currentlyReading">Currently Reading</option>
            <option value="wantToRead">Want to Read</option>
            <option onClick={(e) => changeShelf(e.target.value)} value="read">
              Read
            </option>
            <option value="none">None</option>
          </select>
        </div>
      </div>
      <div className="book-title">{title}</div>
      <div className="book-authors">{authors}</div>
    </div>
  );
}

export default Book;
