import "./App.css";
// import {Provider} from 'react'
import SearchPage from "./components/SearchPage";
import ListofBooks from "./components/ListofBooks";
import { Routes, Route } from "react-router-dom";
import Books from "./context/withFetcbooks";
import GetAllBooks from "./context/GetAllBooks";
import AddBooks from "./context/AddBooks";
import { search } from "./BooksAPI";

function App() {
  const [books, setBooks] = GetAllBooks();
  // const [booksAdded] = AddBooks();
  console.log(
    search({
      title: "Ender's Game",
      authors: "Orson Scott Card",
      image:
        "http://books.google.com/books/content?id=yDtCuFHXbAYC&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE72RRiTR6U5OUg3IY_LpHTL2NztVWAuZYNFE8dUuC0VlYabeyegLzpAnDPeWxE6RHi0C2ehrR9Gv20LH2dtjpbcUcs8YnH5VCCAH0Y2ICaKOTvrZTCObQbsfp4UbDqQyGISCZfGN&source=gbs_api",
      shelf: "none",
    })
  );
  return (
    <Books.Provider value={[books, setBooks]}>
      <div className="app">
        <Routes>
          <Route path="/" element={<ListofBooks />} />
          <Route path="search" element={<SearchPage />} />
        </Routes>
      </div>
    </Books.Provider>
  );
}

export default App;
