import { useState } from "react";
import { update } from "../BooksAPI";

function Book({ book, title, authors, image, value }) {
  const [shelf, setShelf] = useState(value);

  const changeShelf = (e) => {
    setShelf(e.target.value);

    update(book, shelf).then((res) => console.log(res));
    console.log(
      `Shelf Selected is : ${e.target.value} With Title = ${book.title}`
    );
  };
  return (
    <div className="book">
      <div className="book-top">
        <div
          className="book-cover"
          style={{
            width: 128,
            height: 193,
            backgroundImage: `url(${image})`,
          }}
        ></div>
        <div className="book-shelf-changer">
          <select value={shelf || "none"} onChange={ changeShelf}>
            <option value="null" disabled>
              Move to...
            </option>
            <option value="currentlyReading">Currently Reading</option>
            <option value="wantToRead">Want to Read</option>
            <option value="read">Read</option>
            <option value="none">None</option>
          </select>
        </div>
      </div>
      <div className="book-title">{title}</div>
      <div className="book-authors">{authors}</div>
    </div>
  );
}

export default Book;
