import React, { useState } from "react";
// import Books from "../context/withFetcbooks";
import { Link } from "react-router-dom";
import { search } from "../BooksAPI";
import Book from "./Book";

function SearchPage() {
  const [inputValue, setInputValue] = useState("");
  const [matchedResults, setMatchedResults] = useState({
    result: [],
    error: "",
  });
  // const [books] = useContext(Books);

  const getBooksWithSpecificTitle = (updatedValue) => {
    setInputValue(updatedValue);
    search(updatedValue).then((res) => setMatchedResults(res));
  };
  // const commonBooks = books.books.filter(
  //   (x) =>
  //     matchedResults &&
  //     matchedResults.length > 0 &&
  //     matchedResults.map((b) => x === b)
  // );
  // const uncommonBooks = books.books.map(
  //   (x) =>
  //     matchedResults &&
  //     matchedResults.length > 0 &&
  //     matchedResults.filter((b) => b !== x)
  // );

  return (
    <div className="search-books">
      <div className="search-books-bar">
        <Link to={"/"} className="close-search">
          Close
        </Link>
        <div className="search-books-input-wrapper">
          <input
            type="text"
            value={inputValue}
            onChange={(e) => getBooksWithSpecificTitle(e.target.value)}
            placeholder="Search by title, author, or ISBN"
          />
        </div>
      </div>
      <div className="search-books-results">
        <ol className="books-grid">
          {matchedResults && matchedResults.result.length > 0 ? (
            matchedResults.map((b) => {
              return (
                <li key={b.id}>
                  <Book
                    book={b}
                    title={b.title}
                    authors={b.authors[0]}
                    image={b.imageLinks.smallThumbnail}
                    value={b.shelf}
                  />
                </li>
              );
            })
          ) : (
            <h1>Search For Books</h1>
          )}
        </ol>
      </div>
    </div>
  );
}

export default SearchPage;
